import { Component, Input } from '@angular/core';

declare var Razorpay: any;

@Component({
  selector: 'app-razorpay',
  templateUrl: './razorpay.component.html',
  styleUrls: ['./razorpay.component.css']
})
export class RazorpayComponent {
  @Input() amount: number = 0;
  razorpayOptions: any = {
    key: 'rzp_test_zUGVgemrGsmL8z',
    amount: Math.floor(this.amount),
    currency: 'INR',
    name: 'MedX',
    description: 'Purchase Description',
    image: 'https://your-company-logo-url',
    handler: (response: any) => {
      this.razorpaySuccessHandler(response);
    },
    prefill: {
      name: 'Siddhanth Sinha',
      email: 'siddhanthsinha1401@gmail.com'
    },
    notes: {
      address: 'Razorpay Corporate Office'
    },
    theme: {
      color: '#F37254'
    }
  };

  initiatePayment() {
    const options = {
      ...this.razorpayOptions,
      modal: {
        ondismiss: () => {
          this.razorpayFailureHandler();
        }
      }
    };
    console.log(this.amount)
    Razorpay.open(options);
  }

  razorpaySuccessHandler(response: any) {
    console.log(response);
    // Handle success callback
    alert('Payment successful!');
  }

  razorpayFailureHandler() {
    // Handle failure callback
    alert('Payment failed!');
  }
}
<div class="center-container">
  <button (click)="initiatePayment()">Pay with Razorpay</button>
  </div>
  <div id="razorpay-failure" style="display: none;">
    <h2>Payment Failed!</h2>
    <p>Please try again.</p>
  </div>

  <div id="razorpay-error" style="display: none;">
    <h2>Payment Error</h2>
    <p>An error occurred during the payment process.</p>
  </div>
