import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { BehaviorSubject } from 'rxjs';
import { CookieService } from 'ngx-cookie-service';
import { HttpClient } from '@angular/common/http';
@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private isLoggedInSubject: BehaviorSubject<boolean> = new BehaviorSubject<boolean>(false);
  public isLoggedIn$: Observable<boolean> = this.isLoggedInSubject.asObservable();
  status : boolean =false
  document: Document = window.document!;



  constructor(private http: HttpClient,private cookieService: CookieService) {}

  public isLoggedIn(): boolean {
    // check if user is logged in
    console.log(this.isLoggedInSubject.asObservable())
    return this.status
  }

  public registerUser(name: string , email: string , mobileno: String ,password: string){
    const authData  = {data:{name:name , email:email , mobileno:mobileno , password:password}};
    return this.http.post<any>("http://localhost:3000/user/register", authData, { observe: 'response' });
  }

  public login(email:String,password:String) {
    // login user
    const body={data:{email:email,password:password}}
    const response=this.http.post<any>("http://localhost:3000/user/login", body, {observe:'response',withCredentials: true})
    response.subscribe(response=>{
      const body=response.body
      if(body.status==="success"){
        console.log('response',response)
        console.log(this.cookieService.get('userData'))
        const userData = JSON.parse(this.cookieService.get('userData'))
        localStorage.setItem('user', userData.email);
        localStorage.setItem('cart',userData.cart);
        this.isLoggedInSubject.next(false);
      }
      else{
        alert(body.data)
      }
      });
  }


  public logout(): void {
    // logout user
    this.status=false;
    this.isLoggedInSubject.next(false);
  }
}
