import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'src';
  sideNavStatus:boolean = false;
  mainDisplayArea = document.getElementById("mainDisplayArea") as HTMLDivElement;
  constructor() { }

  ngOnInit(): void {
    // Initialization code goes here
    this.mainDisplayArea.addEventListener("click",(): void => {
      this.sideNavStatus=true;
    });

  }

}
<div class="container-fluid g-0">
  <app-header (sideNavToggled)="sideNavStatus=$event;"></app-header>
  <main id="mainDisplayArea">
    <app-sidebar [sideNavStatus]="sideNavStatus"
    [ngClass]="{'app-side-nav-open':sideNavStatus}"></app-sidebar>
    <div class="display-area" [ngClass]="{'display-area-shrink':sideNavStatus}">
      <router-outlet></router-outlet>
    </div>
  </main>
</div>
